import {FETCH_PHONES_SUCCESS,LOAD_MORE_PHONES_SUCCESS} from 'actions';
import R from 'ramda';

const initialState = {};
export default (state = initialState,{type,payload}) => {
    switch(type){
        case FETCH_PHONES_SUCCESS:
            const newValue = R.indexBy(R.prop('id'), payload)
            return R.merge(state, newValue)
        case LOAD_MORE_PHONES_SUCCESS:
            const loadValue = R.indexBy(R.prop('id'), payload)
            return R.merge(state, loadValue)
        default:
            return state;
    }
    
}
