import R from 'ramda'

import {
  FETCH_PHONES_SUCCESS,
  LOAD_MORE_PHONES_SUCCESS,
  SEARCH_PHONE
} from 'actions'

const initialState = {
  ind: [],
  search: ''
}

export default (state = initialState, {type, payload}) => {
  switch (type) {
    case FETCH_PHONES_SUCCESS:
      return R.merge(state, {
        ind: R.pluck('id', payload)
      })
    case LOAD_MORE_PHONES_SUCCESS:
      const ind = R.pluck('id', payload)
      return R.merge(state, {
        ind: R.concat(ind, state.ind)
      })
    case SEARCH_PHONE:
      return R.merge(state, {
        search: payload
      })

    default:
      return state
  }
}
